mapper.select:
SELECT COUNT(*)
FROM animal

	WHERE mem_no=#{mem_no}

SELECT ani.*, mem_name, mem_nickname FROM animal ani JOIN member mem ON ani.mem_no = mem.mem_no
WHERE mem.mem_no=#{mem_no}
ORDER BY ani_no DESC

SELECT ani.*, mem_name, mem_nickname
FROM animal ani JOIN member mem ON ani.mem_no = mem.mem_no
WHERE ani_no=#{ani_no}

mapper.update:
UPDATE animal
SET ani_name=#{ani_name}, ani_age=#{ani_age}, ani_gender=#{ani_gender}, ani_species=#{ani_species}, ani_kind=#{ani_kind}, ani_pic=#{ani_pic}
WHERE ani_no=#{ani_no}
