mapper.select:
SELECT COUNT(*) FROM ( SELECT tn.*, mem_nickname, animal_name FROM market_${table_name} tn LEFT JOIN member mb ON tn.mem_no=mb.mem_no JOIN animal_category ac ON tn.animal_code=ac.animal_code
WHERE tn.animal_code=#{animal_code} AND ${searchColumn} like '%'||#{searchWord}||'%'
WHERE tn.animal_code=#{animal_code}
WHERE ${searchColumn} like '%'||#{searchWord}||'%'
)

SELECT * FROM ( SELECT T.*, ROWNUM R FROM ( SELECT tn.*, mem_nickname, animal_name FROM market_${table_name} tn LEFT JOIN member mb ON tn.mem_no=mb.mem_no JOIN animal_category ac ON tn.animal_code=ac.animal_code
WHERE tn.animal_code=#{animal_code} AND ${searchColumn} like '%'||#{searchWord}||'%'
WHERE tn.animal_code=#{animal_code}
WHERE ${searchColumn} like '%'||#{searchWord}||'%'
ORDER BY no DESC ) T ) WHERE R BETWEEN #{start} AND #{end}

mapper.update:
UPDATE market_${table_name}
SET animal_code=#{animal_code}, title=#{title}, content=#{content}
WHERE no=#{no}

UPDATE market_${table_name}
SET count=count + 1
WHERE no=#{no}
