mapper.select:
SELECT COUNT(*) FROM ( SELECT fb.*, mem_nickname FROM freeboard fb LEFT JOIN member m ON fb.mem_no=m.mem_no
WHERE ${searchColumn} like '%'||#{searchWord}||'%'
)

SELECT * FROM ( SELECT T.*, ROWNUM R FROM ( SELECT fb.*, mem_nickname FROM freeboard fb LEFT JOIN member m ON fb.mem_no=m.mem_no
WHERE ${searchColumn} like '%'||#{searchWord}||'%'
ORDER BY no DESC ) T ) WHERE R BETWEEN #{start} AND #{end}

SELECT fb.*, mem_nickname
FROM freeboard fb LEFT JOIN member mb ON fb.mem_no = mb.mem_no
WHERE no=#{no}

mapper.update:
UPDATE freeboard
SET free_title=#{free_title}, free_content=#{free_content}, free_category=#{free_category}
WHERE no=#{no}

UPDATE freeboard
SET free_hit=free_hit + 1
WHERE no=#{no}

UPDATE freeboard
SET free_count=free_count + 1
WHERE no=#{no}
