mapper.select:
SELECT COUNT(*) FROM ( SELECT nt.*, am_id FROM notice nt LEFT JOIN admin_member am ON nt.am_no=am.am_no
WHERE ${searchColumn} like '%'||#{searchWord}||'%'
)

SELECT * FROM ( SELECT T.*, ROWNUM R FROM ( SELECT nt.*, am_id FROM notice nt LEFT JOIN admin_member am ON nt.am_no=am.am_no
WHERE ${searchColumn} like '%'||#{searchWord}||'%'
ORDER BY nt.no DESC ) T ) WHERE R BETWEEN #{start} AND #{end}

SELECT nt.*, am_id
FROM notice nt LEFT JOIN admin_member am ON nt.am_no = am.am_no
WHERE no=#{no}

mapper.update:
UPDATE notice
SET title=#{title}, content=#{content}
WHERE no=#{no}

UPDATE notice
SET count=count + 1
WHERE no=#{no}
