mapper.select:
SELECT COUNT(*) FROM ( SELECT t.*, mem_nickname FROM tip t JOIN member m on t.mem_no = m.mem_no WHERE ani_category=#{ani_category}
AND ${searchColumn} like '%'||#{searchWord}||'%'
)

SELECT *
FROM (
	SELECT T.*, ROWNUM R
	FROM (
		SELECT t.*, mem_nickname
		FROM tip t JOIN member m ON t.mem_no=m.mem_no
		WHERE ani_category=#{ani_category}
		
			AND ${searchColumn} like '%'||#{searchWord}||'%'
		
		ORDER BY tip_no DESC
		) T
	)
WHERE R BETWEEN #{start} AND {#end}

SELECT tn.*, mem_nickname
FROM tip tn JOIN member mb ON tn.mem_no = mb.mem_no
WHERE tip_no=#{tip_no}

mapper.update:
UPDATE tip
SET tip_title=#{tip_title}, tip_content=#{tip_content}
WHERE tip_no=#{tip_no}
