mapper.select:
SELECT COUNT(*)
FROM member
WHERE mem_id=#{mem_id} AND mem_pw=#{mem_pw}

SELECT COUNT(*)
FROM member

	
		WHERE mem_nickname = #{mem_nickname}
	
	
		WHERE mem_id = #{mem_id}

SELECT *
FROM (
	SELECT T.*, ROWNUM R
	FROM (
		SELECT *
		FROM member
		ORDER BY mem_no
		) T
	)
WHERE R BETWEEN #{start} AND #{end}

SELECT *
FROM member

	WHERE mem_no=#{mem_no}


	WHERE mem_id=#{mem_id}

mapper.update:
UPDATE member
SET mem_id=#{mem_id}, mem_pw=#{mem_pw}, mem_name=#{mem_name}, mem_nickname=#{mem_nickname}, mem_gender=#{mem_gender}, mem_interani=#{mem_interani}
WHERE mem_no=#{mem_no}
