mapper.select:
SELECT COUNT(*) FROM ( SELECT mo.*, mem_nickname FROM movie mo JOIN member me ON mo.mem_no=me.mem_no WHERE ani_category=#{ani_category}
AND ${searchColumn} like '%'||#{searchWord}||'%'
)

SELECT *
FROM (
	SELECT T.*, ROWNUM R
	FROM (
		SELECT mo.*, mem_nickname
		FROM movie mo JOIN member me ON mo.mem_no=me.mem_no
		WHERE ani_category=#{ani_category}
		
			AND ${searchColumn} like '%' || #{searchWord} || '%'
		
		ORDER BY movie_no DESC
		) T
	)
WHERE R BETWEEN #{start} AND #{end}

SELECT tn.*, mem_nickname
FROM movie tn JOIN member mb ON tn.mem_no = mb.mem_no
WHERE movie_no=#{movie_no}

mapper.update:
UPDATE movie
SET movie_title=#{movie_title}, movie_content=#{movie_content}
WHERE movie_no=#{movie_no}
